import axios from 'axios';


const API_key = process.env.REACT_APP_API_KEY;

function getCurrentWeather(location){
    return axios.get(
        `http://api.openweathermap.org/data/2.5/weather?q=${location}&units=metric&appid=${API_key}`
    );
}


export{
    getCurrentWeather
}
