import React from 'react';
import './search-bar.scss';

//this is a class component
class SearchBar extends React.Component{
    
    
      onInputChange(e){
          this.props.inputChange(e);
      }
      onFormSubmit(e){
          e.preventDefault();
          this.props.formsubmitted(e);
      }
      onHandleChange(e){
          this.props.handleChange(e);
      }
    
      

    /**
     * render function is triggered whenever updating the state using set State function 
     *<select id="units" name="units">
                        <option value="metric">Celsius</option>
                        <option value="imperial">Fahrenheit</option>
                    </select>
     */

    render(){
        const location = this.props.location;
        const selectValue = this.props.value;

        return (
            <div className="py-20 h-screen bg-gray-300 px-2">
                <form className="max-w-md mx-auto rounded-lg overflow-hidden md:max-w-xl" onSubmit={(e) => this.onFormSubmit(e)}>
                        <button className="flex items-center justify-center px-4 border-r" type="submit">
                            <svg className="w-6 h-6 text-gray-600" fill="currentColor" xmlns="http://www.w3.org/2000/svg"
                                    viewBox="0 0 24 24">
                                    <path
                                        d="M16.32 14.9l5.39 5.4a1 1 0 0 1-1.42 1.4l-5.38-5.38a8 8 0 1 1 1.41-1.41zM10 16a6 6 0 1 0 0-12 6 6 0 0 0 0 12z">
                                    </path>
                                </svg>
                        </button>
                        <input className="px-4 py-2 w-80" type="text" id="search" placeholder="City" value={location} onChange={e => this.onInputChange(e)}/>
                        <select id="units" name="units" value={selectValue} onHandleChange={e => this.onHandleChange(e)}>
                            <option value="metric">Celsius</option>
                            <option value="imperial">Fahrenheit</option>
                        </select>
                    

                    
                    
                </form>
            </div>
        );
    }
}


export default SearchBar;
