import axios from 'axios';


const API_key = process.env.REACT_APP_API_KEY;


function getCurrentWeather(location, selectValue){
    return axios.get(
        `http://api.openweathermap.org/data/2.5/weather?q=${location}&units=${selectValue}&appid=${API_key}`
    );
}

function getForecast(lat, lon){

    return axios.get(
        `http://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&units=metric&appid=${API_key}`
    )
}


export{
    getCurrentWeather,
    getForecast
}
