import React from "react";
import {Bar} from 'react-chartjs-2';


class LineChart extends React.Component{
    render(){
        const hourTime = this.props.hour_forecast.map( (f, i) => {

            
            let hour = new Date(f.dt * 1000).getHours();
            console.log(hour);
            return(
                    {hour}
            );
        });
        const hourTemp = this.props.hour_forecast.map( (f, i) => {

            
            let temp = f.temp;
            
            return(
                    {temp}
            );
        });
        const hours = [{hourtime}];
        console.log(hours);
        console.log(hourTemp);
        return(
            
            <div>
                <Bar
                
                    data={{ 
                        labels: [{hourTime}],
                        datasets:[
                            {
                                label: 'Evolution of Day Temperature',
                                data: [12, 19, 3, 5, 2, 3],//[{hourTemp}],
                                backgroundColor: [
                                    'rgba(255, 99, 132, 0.2)',
                                    'rgba(54, 162, 235, 0.2)',
                                    'rgba(255, 206, 86, 0.2)',
                                    'rgba(75, 192, 192, 0.2)',
                                    'rgba(153, 102, 255, 0.2)',
                                    'rgba(255, 159, 64, 0.2)'
                                ],
                                borderColor: [
                                    'rgba(255, 99, 132, 1)',
                                    'rgba(54, 162, 235, 1)',
                                    'rgba(255, 206, 86, 1)',
                                    'rgba(75, 192, 192, 1)',
                                    'rgba(153, 102, 255, 1)',
                                    'rgba(255, 159, 64, 1)'
                                ],
                                borderWidth: 1

                            }
                        ]
                     }}
                    height={400}
                    width={600}
                    options={{ 
                        maintainAspectRatio: false,
                     }}

                    />
            </div>

        );

    };


}


export default LineChart;
