import React from 'react';
import './search-bar.scss';

//this is a class component
class SearchBar extends React.Component{
    
    
      onInputChange(e){
          this.props.inputChange(e);
      }
      onFormSubmit(e){
          e.preventDefault();
          this.props.formsubmitted(e);
      }
      onHandleChange(e){
          this.props.handleChange(e);
      }
    
      

    /**
     * render function is triggered whenever updating the state using set State function 
     *<select id="units" name="units">
                        <option value="metric">Celsius</option>
                        <option value="imperial">Fahrenheit</option>
                    </select>
     */

    render(){
        const location = this.props.location;
        const selectValue = this.props.value;

        return (
            <div className="container flex justify-center items-center">
                <form className="search-bar__form" onSubmit={(e) => this.onFormSubmit(e)}>
                    <div className="relative">
                        <div className="absolute top-4 left-3">
                            <i class="fa fa-search text-gray-400 z-20 hover:text-gray-500"/>
                        </div>
                        <input className="h-14 w-96 pl-10 pr-20 rounded-lg z-0 focus:shadow focus:outline-none" type="text" id="search" placeholder="City" value={location} onChange={e => this.onInputChange(e)}/>
                        <div className="absolute top-2 right-2">

                        </div>
                    <button className="h-10 w-20 text-white rounded-lg bg-red-500 hover:bg-red-600" type="submit">
                        Search
                    </button>
                    <select id="units" className="search-bar__select" value={selectValue}  onChange={e => this.onHandleChange(e)}>
                        <option value="metric" className="search-bar__option_one">Celsius</option>
                        <option value="imperial" className="search-bar__option_two" >Fahrenheit</option>
                    </select>
                    </div>
                    
                </form>
            </div>
        );
    }
}


export default SearchBar;
