import React from 'react';

import './App.scss';

import SearchBar from './components/search-bar';
import CurrentWeather from './components/current-weather';
import Forecast from './components/forecast-weather';

import {getCurrentWeather, getForecast} from './apis/open-weather.api';

//functional component
//returns template
class App extends React.Component{

  constructor(props){
    super(props);
    this.state = {
        location: 'New York',
        temp: '',
        feelsLike: '',
        description: '',
        icon: '',
    };
  }

  onInputChange(e){
      this.setState({
          location: e.target.value
      });
  }
  async onFormSubmit(){
    const weatherRes = await getCurrentWeather(this.state.location);

    const lat = weatherRes.data.coord.lat;
    const lon = weatherRes.data.coord.lon;

    const forecastRes = await getForecast();

    //      this.setState({
      //          temp: response.data.main.temp,
      //          feelsLike: response.data.main.feelsLike,
      //          description: response.data.weather[0].main,
      //          icon: response.data.weather[0].icon,
      //      })
  }

  render(){
    return (
      <div className="App">
        <header className="App-header">
          <SearchBar 
                location={this.state.location} 
                inputChange= {e => this.onInputChange(e)}
                formsubmitted={() => this.onFormSubmit()}
          />
          <CurrentWeather 
                currentTemperature={this.state.temp}
                feelsLike={this.state.feelsLike}
                description={this.state.description}
                icon={this.state.icon}      
          />

          <Forecast />
        </header>
      </div>
    );
  }
}

export default App;
