import React from "react";


class Forecast extends React.Component{
    render(){
        const forecastItems = this.props.forecast.map( (f, i) => {
            const url = `http://openweathermap.org/img/wn/${f.weather[0].icon}@2x.png`;
            let ampm = 'AM';
            let date = new Date(f.dt * 1000).toDateString();
            let hour = new Date(f.dt * 1000).getHours();
            if(hour > 12){
                hour = hour-12;
                ampm = 'PM';
            } 
            return(
                <div className="forecast-item" key={i}>
                    <p className="forecast-item__date">
                        {date}
                    </p>
                    <p className="forecast-item__hour">
                        {hour}:00 {ampm}{" "}
                    </p>
                    <p className="forecast-item__temp">
                        {f.temp.day}
                    </p>
                    <img className="forecast-item__icon" src={url} alt={f.weather[0].description}/>
                        
                    <p className="forecast-item__feelslike">
                        Feels Like: {f.feels_like.day}
                    </p>
                    <p className="forecast-item__description">
                        {f.weather[0].main}
                    </p>
                </div>
            );
        });

        return(
            <div className="forecast">
                {forecastItems}
            </div>
        );
    }
}


export default Forecast;
