import React from "react";
import './forecast-weather.scss';

class Forecast extends React.Component{
    render(){
        const forecastItems = this.props.forecast.map( (f, i) => {
            if(1<=i<=5 ){
                const key = `forecast-item_${i}`;
            const url = `http://openweathermap.org/img/wn/${f.weather[0].icon}@2x.png`;
            let ampm = 'AM';
            let date = new Date(f.dt * 1000).toDateString();
            let hour = new Date(f.dt * 1000).getHours();
            if(hour > 12){
                hour = hour-12;
                ampm = 'PM';
            } 
            return(
                <div className="text-center justify-between items-center inline-block  rounded-2xl my-2 mx-2 shadow-md bg-gray-900" key={key}>
                    <div className="text-center mb-0 flex items-center justify-center flex-col mx-auto">
                        <span className="block my-1 font-">{date}</span>
                        <p className="block my-1">
                            {hour}:00 {ampm}{" "}
                        </p>
                        <img className="block" src={url} alt={f.weather[0].description}/>
                        <p className="block my-1">
                            {f.temp.day}&deg;
                        </p>
                        <p className="block my-1">
                            <span className="text-sm">Feels Like: {f.feels_like.day}&deg;</span>
                        </p>
                        <p className="block my-1">
                            {f.weather[0].main}
                        </p>
                    </div>
                </div>
            );
            }else{
                
            }
            
        });

        return(

        <div className="w-full px-2">
            <div className="bg-gradient-to-r from-blue-200 to-blue-800 text-white relative min-w-0 break-words rounded-lg overflow-hidden shadow-sm mb-4 w-full  dark:bg-gray-600">
                <div className="divider table mx-2 text-center bg-transparent whitespace-nowrap">
                    <h3 className="inline-block px-3">Daily Forecast</h3>
                </div>
                
                <div className="px-6 py-6 relative ">
                    {forecastItems}
                </div>
                
            </div>
        </div>
        );
    }
}


export default Forecast;
