import React from 'react';


//this is a class component
class SearchBar extends React.Component{

    constructor(props){
        super(props);

        this.state = {
            location: ''
        };

        
    }


    onInputChange(e){
console.log('calling on input change');
    }


    /**
     * render function is triggered whenever updating the state using set State function 
     *
     */

    render(){
        const location = this.state.location;

        return (
            <form>
                {location}
                <input type="text" id="search" placeholder="City" value={location} onChange={e => this.onInputChange(e)}/>
                <button type="submit">
                    Search
                </button>
            </form>
        );
    }
}


export default SearchBar;
