import React from "react";
import {Bar} from 'react-chartjs-2';


class LineChart extends React.Component{
    render(){
        const hourTime = this.props.hour_forecast.map( (f, i) => {

            
            let hour = new Date(f.dt * 1000).getHours();

            return(
                    {hour}
            );
        });
        const hourTemp = this.props.hour_forecast.map( (f, i) => {

            
            let temp = f.temp;

            return(
                    {temp}
            );
        });
        return(
            <div>
                <Bar
                
                    data={{ 
                        labels: [{hourTime}],
                        datasets:[
                            {
                                label: 'Evolution of Day Temperature',
                                data:[{hourTemp}],
                            }
                        ]
                     }}
                    height={400}
                    width={600}
                    options={{ 
                        maintainAspectRatio: false,
                     }}

                    />
            </div>

        );

    };


}


export default LineChart;
