import React from "react";
import {Line} from 'react-chartjs-2';


class LineChart extends React.Component{
    


    render(){

        let h = [];
        let t = [];
        let i;
        const hourTime = [this.props.hour_forecast.map( (f) => {

            
            let hour = new Date(f.dt * 1000).getHours();
            
            return(
                    {hour}
            );
        })];
        for(i = 0; i<24; i++){
            
            h.push(hourTime[0][i]);
        }
        const hourTemp = [this.props.hour_forecast.map( (f) => {

            
            let temp = f.temp;
            temp = dataObj.temp;
            
            return(
                    {temp}
            );
        })];
        for(i = 0; i<24; i++){
            
            t.push(parseFloat(hourTemp[0][i]));
        }
        
        console.log(t);
        return(
            
            <div>
                <Line
                
                    data={{ 
                        labels:  [{h}],
                        datasets:[
                            {
                                label: 'Evolution of Day Temperature',
                                data: [{t}],
                                backgroundColor: [
                                    'rgba(255, 99, 132, 0.2)',
                                    'rgba(54, 162, 235, 0.2)',
                                    'rgba(255, 206, 86, 0.2)',
                                    'rgba(75, 192, 192, 0.2)',
                                    'rgba(153, 102, 255, 0.2)',
                                    'rgba(255, 159, 64, 0.2)'
                                ],
                                borderColor: [
                                    'rgba(255, 99, 132, 1)',
                                    'rgba(54, 162, 235, 1)',
                                    'rgba(255, 206, 86, 1)',
                                    'rgba(75, 192, 192, 1)',
                                    'rgba(153, 102, 255, 1)',
                                    'rgba(255, 159, 64, 1)'
                                ],
                                borderWidth: 1

                            }
                        ]
                     }}
                    height={400}
                    width={600}
                    options={{ 
                        maintainAspectRatio: false,
                        
                     }}

                    />
            </div>

        );

    };


}


export default LineChart;
