import React, {useState, useEffect} from "react";
import {Line} from 'react-chartjs-2';



class LineChart extends React.Component{
    


    render(){
        let h = [];
        let t = [];
        let i;
        const hourTime = [this.props.hour_forecast.map( (f) => {
            let hour = new Date(f.dt * 1000).getHours();
            h.push(hour);
            return(
                    {hour}
            );
        })];

        const hourTemp = [this.props.hour_forecast.map( (f) => {

            
            let temp = f.temp;
            t.push(temp);
            return(
                    {temp}
            );
        })];
        console.log(h);
        console.log(t);
        
        const linedata = {
            labels: h,
            datasets: [
                {
                    data: t,
                    strokeWidth: 2,
                }
            ]
        }
        return(
            <div>
                <Line
                
                    data={{ 
                        labels:  ['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
                        datasets:[
                            {
                                label: 'Evolution of Day Temperature',
                                data: [12, 19, 3, 5, 2, 3],
                                backgroundColor: [
                                    'rgba(255, 99, 132, 0.2)',
                                    'rgba(54, 162, 235, 0.2)',
                                    'rgba(255, 206, 86, 0.2)',
                                    'rgba(75, 192, 192, 0.2)',
                                    'rgba(153, 102, 255, 0.2)',
                                    'rgba(255, 159, 64, 0.2)'
                                ],
                                borderColor: [
                                    'rgba(255, 99, 132, 1)',
                                    'rgba(54, 162, 235, 1)',
                                    'rgba(255, 206, 86, 1)',
                                    'rgba(75, 192, 192, 1)',
                                    'rgba(153, 102, 255, 1)',
                                    'rgba(255, 159, 64, 1)'
                                ],
                                borderWidth: 1

                            }
                        ]
                     }}
                    height={400}
                    width={600}
                    options={{ 
                        maintainAspectRatio: false,
                        
                     }}

                    />
            </div>

        );

    };


}

export default LineChart;
