import React from 'react';
import './current-weather.scss';

class CurrentWeather extends React.Component{
    render(){
        let img;
        if(this.props.icon){
            const url = `http://openweathermap.org/img/wn/${this.props.icon}@4x.png`;
            img = (<img className="current-weather__icon" src={url} alt={this.props.description}/>);
        }
        
        return(
            <div className="bg-gray-900 text-white relative min-w-0 break-words roundend-lg overflow-hidden shadow-sm mb-4 w-full dark:bg-gray-600">
                <div className="px-6 py-6 relative">

                    <div className="flex mb-4 justify-between items-center">
                        <div className="text-left">
                            <h5 className=" mb-0 font-medium text-xl">{this.props.city}</h5>
                            <h6 className="mb-0">November 19 2021</h6><small>{this.props.description}</small>
                        </div>
                    </div>

                    <div className="current-weather__content">
                        <div className="current-weather__text">
                            <p className="current-weather__temp">
                                {this.props.currentTemperature}&deg;

                            </p>
                            <p className="current-weather__description">
                                {this.props.description}
                            </p>
                        </div>
                        {img}
                    </div>
                    <div>
                        <p className="current-weather__feels-like">
                            Feels Like: {this.props.feelsLike}&deg;
                        </p>
                    </div>
                </div>
            </div>

        );
    }

}


export default CurrentWeather;
