import React from 'react';
import './search-bar.scss';

//this is a class component
class SearchBar extends React.Component{
    
    
      onInputChange(e){
          this.props.inputChange(e);
      }
      onFormSubmit(e){
          e.preventDefault();
          this.props.formsubmitted(e);
      }
      onHandleChange(e){
          this.props.handleChange(e);
      }
    


    /**
     * render function is triggered whenever updating the state using set State function 
     *<select id="units" name="units">
                        <option value="metric">Celsius</option>
                        <option value="imperial">Fahrenheit</option>
                    </select>
     */

    render(){
        const location = this.props.location;
        const selectValue = this.props.selectValue;

        return (
            <div className="search-bar">
                <form className="search-bar__form" onSubmit={(e) => this.onFormSubmit(e)}>
                    <button className="search-bar__button" type="submit">
                        Search
                    </button>
                    <input className="search-bar__input" type="text" id="search" placeholder="City" value={location} onChange={e => this.onInputChange(e)}/>
                    <select id="units" name="units" value={selectValue} onChange={e => this.onHandleChange(e)}>
                        <option value="metric" >Celsius</option>
                        <option value="imperial">Fahrenheit</option>
                    </select>
                </form>
            </div>
        );
    }
}


export default SearchBar;
