import React from 'react';
import './current-weather.scss';

class CurrentWeather extends React.Component{
    render(){
        let img;
        if(this.props.icon){
            const url = `http://openweathermap.org/img/wn/${this.props.icon}@4x.png`;
            img = (<img className="current-weather__icon" src={url} alt={this.props.description}/>);
        }
        let min = Math.floor(this.props.minTemperature, 1)
        
        return(
            <div className="bg-gray-900 text-white relative min-w-0 break-words rounded-lg overflow-hidden shadow-sm mb-4  dark:bg-gray-600">
                <div className="px-6 py-6 relative">

                    <div className="flex mb-4 justify-between items-center">
                        <div className="text-left">
                            <h5 className=" mb-0 font-medium text-xl">{this.props.city}</h5>
                            <h6 className="mb-0">November 19 2021</h6><small>{this.props.description}</small>
                        </div>
                        {img}
                        <div className="text-right">
                            <h3 className="font-bold text-4xl mb-0"><span>{this.props.currentTemperature}&deg;</span></h3>
                        </div>
                    </div>
                    <div className="block sm:flex text-sm justify-between items-center flex-wrap">
                        <div className="w-full sm:w-1/2">
                            <div className="flex mb-2  justify-between items-center">
                                <span>Temperature: </span><small className="px-2 inline-block">{this.props.currentTemperature}&deg;</small>
                            </div>
                        </div>
                        <div className="w-full sm:w-1/2">
                            <div className="flex mb-2 justify-between items-center">
                                <span>Feels like: </span><small className="px-2 inline-block">{this.props.feelsLike}&deg;</small>
                            </div>
                        </div>
                        <div className="w-full sm:w-1/2">
                            <div className="flex mb-2 justify-between items-center">
                                <span>Min. Temperature: </span><small className="px-2 inline-block">{this.props.minTemperature}&deg;</small>
                            </div>
                        </div>
                        <div className="w-full sm:w-1/2">
                            <div className="flex mb-2 justify-between items-center">
                                <span>Max. Temperature: </span><small className="px-2 inline-block">{this.props.maxTemperature}&deg;</small>
                            </div>
                        </div>
                    </div>
                    

                   
                </div>
            </div>

        );
    }

}


export default CurrentWeather;
